import React, { useState } from "react";
import "./App.css";
import { Quizzer } from "./quizzer/Quizzer";
import { Button } from "react-bootstrap";
import { QuizQuestion } from "./quizzer/QuizQuestion";
import { Question, QuestionType } from "./interfaces/question";
import { QuestionEdit } from "./quizzer/QuestionEdit";
import { QuizEdit } from "./quizzer/QuizEdit";
//import { ShowHideTasks } from "./tasks/ShowHideTasks";

function App(): JSX.Element {
    const [quizzer, setQuizzer] = useState<boolean>(true);

    function makeBlankQuestion(
        id: number,
        name: string,
        type: QuestionType
    ): Question {
        return {
            id: id,
            body: "",
            type: type,
            options: [],
            submission: "",
            expected: "",
            points: 1,
            published: false
        };
    }
    function makeBlankQuiz() {
        return {
            id: 0,
            title: "",
            body: "",
            published: false,
            questionList: []
        };
    }

    function testScript() {
        console.log("test");
    }

    return (
        <div className="App">
            <header className="App-header">
                UD CISC275 with React Hooks and TypeScript
                <Button
                    onClick={() => {
                        setQuizzer(!quizzer);
                    }}
                >
                    {quizzer ? "Quizzer" : "Tasks"}
                </Button>
            </header>
            <QuizEdit
                quiz={makeBlankQuiz()}
                editQuiz={testScript}
                deleteQuiz={testScript}
                switchEdit={testScript}
                resetView={testScript}
            ></QuizEdit>
        </div>
    );
}
//<div style={{ display: quizzer ? "block" : "none" }}>
//<Quizzer></Quizzer>
//</div>

export default App;
