import React, { useState } from "react";
import "./App.css";
import { Quizzer } from "./quizzer/Quizzer";
import { Button } from "react-bootstrap";
import { QuizQuestion } from "./quizzer/QuizQuestion";
import { Question, QuestionType } from "./interfaces/question"
import { QuestionEdit } from "./quizzer/QuestionEdit";
//import { ShowHideTasks } from "./tasks/ShowHideTasks";

function App(): JSX.Element {
    const [quizzer, setQuizzer] = useState<boolean>(true);

    function makeBlankQuestion(
        id: number,
        name: string,
        type: QuestionType
    ): Question {
        return {
            id: id,
            body: "",
            type: type,
            options: [],
            submission: "",
            expected: "",
            points: 1,
            published: false
        };
    }

    function testScript(){
        console.log("test");
    }

    return (
        <div className="App">
            <header className="App-header">
                UD CISC275 with React Hooks and TypeScript
                <Button
                    onClick={() => {
                        setQuizzer(!quizzer);
                    }}
                >
                    {quizzer ? "Quizzer" : "Tasks"}
                </Button>
            </header>
            <QuestionEdit>
                index = {1}
                lastIndex = {2}
                question = {makeBlankQuestion(1,"test","short_answer_question")}
                editQuestion = {testScript}
                removeQuestion = {testScript}
                swapQuestion = {testScript}
            </QuestionEdit>
        </div>
    );
}
//<div style={{ display: quizzer ? "block" : "none" }}>
//<Quizzer></Quizzer>
//</div>


export default App;
